# functions to work with the coastal datasetimport osimport gdalimport geopandasfrom ..generic.gis_tools import \    ll2utm, shape2raster, create_crs_from_utm_numberfrom ..generic.handler_sentinel2 import \    get_bbox_from_tile_codefrom ..generic.handler_landsat import \    get_bbox_from_path_row# tile_code = '15NXA' # utm_zone = '15N'   def get_coast_of_tile(tile_code, utm_zone, tile_system='MGRS',\                      resolution=10, \                      shp_dir='/Users/Alten005/surfdrive/Eratosthenes/Coastline', \                      shp_name='GSHHS_f_L6.shp'): # wrs - landsat        aoi = 'land' # attribute of interest        if tile_system=='MGRS':        toi = get_bbox_from_tile_code(tile_code)    else: # WRS2        #wip        path,row = [],[]        toi = get_bbox_from_path_row(path,row)        # transform shapefile from polar coordinates to metric projection    crs = create_crs_from_utm_number(utm_zone)        shp_path = os.path.join(shp_dir,shp_name)    gshhs = geopandas.read_file(shp_path)        mask = gshhs.intersection(toi)    gshhs_sub = gshhs[mask]       